var Node        = require('famous/core/Node');
var DOMElement  = require('famous/dom-renderables/DOMElement');


/*
title：按钮标题；
image1 ：选中后图片；
iamge2 ：未选中图片；
*/

function FooterButton(title,image1,image2)
{
	Node.call(this)
	
	this.image1 = image1;
	this.image2 = image2;
	this.addUIEvent('click');
	this.isSelect = false;

	// this.imageView = {};
	// this.titleLabel ={};

	var content = this.addChild();
		content.setSizeMode(1,1)
			   .setAbsoluteSize(25,38)
			   .setMountPoint(0.5,0.5)
			   .setAlign(0.5,0.5)
			   .el = new DOMElement(this,{classes:['test']});

	var imageView = content.addChild()
		imageView.setSizeMode(1,1)
			 .setAbsoluteSize(27,37)
			 .setMountPoint(0.5,0)
			 .setAlign(0.5,0)
			 .el = new DOMElement(imageView,{tagName:'img'}).setAttribute('src',image2);

	/*var titleLabel = content.addChild();
		titleLabel.setSizeMode(0,1)
				  .setAbsoluteSize(null,12)
				  .setPosition(0,26)
				  .el = new DOMElement(titleLabel).setContent(title)
												  .setProperty('text-align','center')
												  .setProperty('font-size','1.2rem')
												  .setProperty('color','rgb(187,187,187)');*/
	this.imageView = imageView;
	/*this.titleLabel = titleLabel;*/
}

FooterButton.prototype = Object.create(Node.prototype);
FooterButton.prototype.constructor = FooterButton;

FooterButton.prototype.didSelect = function()
{
	this.isSelect = true;
	this.imageView.el = new DOMElement(this.imageView,{tagName:'img'}).setAttribute('src',this.image1);
	/*this.titleLabel.el = new DOMElement(this.titleLabel).setProperty('text-align','center')
													.setProperty('font-size','1.2rem')
													.setProperty('color','rgb(251,160,89)');*/

}

FooterButton.prototype.deSelect = function()
{
	this.isSelect = false;
	this.imageView.el = new DOMElement(this.imageView,{tagName:'img'}).setAttribute('src',this.image2);
	/*this.titleLabel.el = new DOMElement(this.titleLabel).setProperty('text-align','center')
													.setProperty('font-size','1.2rem')
													.setProperty('color','rgb(187,187,187)');*/
}
FooterButton.prototype.onReceive = function(e,p)
{
	if(e==='click'){
		if (p.node._id == this._id) {

			this.clickEvent(e,p);

		};
	}
}

FooterButton.prototype.clickEvent = function(e,p){


};



module.exports = FooterButton;