var Node = require('famous/core/Node');
var DOMElement = require('famous/dom-renderables/DOMElement');
var Button = require('button');

/*
*obj：对象
*i:索引
*height：高度
*
*/

class IconItem extends Node{
	constructor(obj,i,height){
		super()

		this
			.setSizeMode('relative','absolute')
			.setAbsoluteSize(null,height)
			.setPosition(0,i*height)
			.setAlign(.5)
			.setMountPoint(.5)
			.el = new DOMElement(this,{
				classes:['icon_item','fa-button'],
				content:'<div class="inner_item"><img src="'+obj.icon+'"><span>'+obj.desc+'</span></div>'
			})

		this.addUIEvent('touchstart');
		this.addUIEvent('touchend');
	
	}

	onReceive(e,p){
		if(e === 'touchstart'){
			this.el.addClass("bt-active");
		}else if( e === 'touchend'){
			this.el.removeClass("bt-active");
		}
	}
}

module.exports = IconItem;