var Node = require('famous/core/Node');
var DOMElement = require('famous/dom-renderables/DOMElement');
var App = require('App');


var blankArr = {
    'apply':{'img':'./images/blank/apply.png','height':'82.5','txt1':'暂时还没有任何记录','txt2':'您可以返回申请页开始您的记录'},//申请
    'find':{'img':'./images/blank/find.png','height':'143','txt1':'活动正在紧锣密鼓的策划中','txt2':'参加统一举办的活动助力您的好生意'}, //发现策划
    'mes':{'img':'./images/blank/mes.png','height':'135','txt1':'消息正在以火箭般的速度赶来','txt2':'任何重要的信息会第一时间展示在此页面'}, //信息
    'notice':{'img':'./images/blank/notice.png','height':'82.5','txt1':'暂时还没有任何公告','txt2':'任何重要的公告会第一时间展示在此页面'}, //园区公告
    'order':{'img':'./images/blank/order.png','height':'103','txt1':'没有任何点餐记录','txt2':'您的点餐记录都会保存在这个页面'},//订餐
    'business':{'img':'./images/blank/apply.png','height':'82.5','txt1':'暂时还没有商家生涯记录','txt2':'任何重要的生涯记录会第一时间展示在此页面'}//商家记录
};

class blank extends Node{
    constructor(type){
        super();
        var obj = null;
        if(type === 'apply'){
            obj = blankArr.apply;
        }else if(type === 'find'){
            obj = blankArr.find;
        }else if(type === 'mes'){
            obj = blankArr.mes;
        }else if(type === 'notice'){
            obj = blankArr.notice;
        }else if(type === 'order'){
            obj = blankArr.order;
        }else if(type === 'business'){
            obj = blankArr.business;
        }
        createBlank(this,obj);
    }
}

function createBlank(node,obj){
    var content = node.addChild();
    content
        .setSizeMode('relative','absolute')
        .setAlign(.5,.3)
        .setMountPoint(.5,.3)
        .el = new DOMElement(content,{
        classes:['blank_content'],
        content:'<img src="'+obj.img+'" height="'+obj.height+'">'+
        '<p>'+obj.txt1+'</p><p>'+obj.txt2+'</p>'
    });
}

module.exports = blank;