var FamousEngine = require('famous/core/FamousEngine');
var DOMElement   = require('famous/dom-renderables/DOMElement');
var Node = require('famous/core/Node');


function button(buttonText,bgColor,cusClass){
	this.cusClass = typeof cusClass!="undefined"?[cusClass,"fa-button","text-center"]:["fa-button","text-center"];
	Node.call(this);
	this
		.addUIEvent('touchstart')
		.addUIEvent('touchend').addUIEvent("click")
		.el = new DOMElement(this,{
			classes:this.cusClass,
			content:"<input type='button' value='"+buttonText+"' />",
			properties:{"background-color":bgColor}
		});
	this.clickEvent="";
}
button.prototype = Object.create(Node.prototype);
button.prototype.constructor = button;

button.prototype.onReceive = function(e,p){
	if(e=="touchend"){
		this.el.removeClass("bt-active");
	}
	if(e=="touchstart"){
		this.el.addClass("bt-active");
	}
	if(e=="click"){
		if(this.clickEvent){this.clickEvent(e,p);}
	}
}

module.exports = button;