var DOMElement   = require('famous/dom-renderables/DOMElement');
var Node = require('famous/core/Node');
var button = require('button');

function nobackNav(title,leftBtn,rightBtn){
	Node.call(this);
	this
		.setSizeMode(0,1)
		.setAbsoluteSize(null,44)
		.el = new DOMElement(this,{
			classes:["navbar"],
			content:"<p class='font-lg text-center'><span>"+ title +"</span></p>"
		})
	this.leftBtn = this.addChild(leftBtn);
	this.leftBtn.setSizeMode(1,1).setAbsoluteSize(44,44).el = new DOMElement(this.leftBtn)
	this.rightBtn = this.addChild(rightBtn);
	this.rightBtn.setSizeMode(1,1).setAbsoluteSize(44,44).setAlign(1,0).setMountPoint(1,0).el = new DOMElement(this.rightBtn);
}
nobackNav.prototype = Object.create(Node.prototype)
nobackNav.prototype.constructor = nobackNav;
module.exports = nobackNav;;